tinymce.PluginManager.add('kongsbeg', function(editor, url, edits_reps) {
										   
	
    // Add a button that opens a window
    editor.addButton('kongsbeg', {
        text: 'Kongsbeg Reports',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Kongsbeg Reports',
    			url: 'https://kongsberg-reports.com/admin/report_uplords/listMCE',
				width: 1024,
    			height: 600
				},
				{
					
				oninsert: function(url) {
					editor.insertContent(url);
				}
					});
        }
    });

    // Adds a menu item to the tools menu
	
    
});